CustomField.formula:
'$'&

/* Text Thousands Separator under 1 B - preserves decimals, negatives */
/* Under 1 K */
IF( OrderApi__Overall_Total__c < 1000, TEXT(OrderApi__Overall_Total__c),

/* Under 1 M */
IF(OrderApi__Overall_Total__c < 1000000, TEXT(FLOOR(OrderApi__Overall_Total__c /1000))
& ','
& RIGHT(TEXT(OrderApi__Overall_Total__c), LEN(TEXT(OrderApi__Overall_Total__c)) - LEN(TEXT(FLOOR(OrderApi__Overall_Total__c /1000)))),

/* Under 1 B */
IF(OrderApi__Overall_Total__c < 1000000000, TEXT(FLOOR(OrderApi__Overall_Total__c /1000000))
& ','
& MID(TEXT(OrderApi__Overall_Total__c), 1 + LEN(TEXT(FLOOR(OrderApi__Overall_Total__c /1000000))),3)
& ','
& RIGHT(TEXT(OrderApi__Overall_Total__c), LEN(TEXT(OrderApi__Overall_Total__c)) - LEN(TEXT(FLOOR(OrderApi__Overall_Total__c /1000)))),

/* 1 B & up */
IF(OrderApi__Overall_Total__c >= 1000000000, "#TooHigh!", NULL))))
